-- | listas de estados
listaMapas :: [Estado]
listaMapas = [estadoTesteRender1,estadoTesteRender2,estadoTesteRender3,estadoTesteRender4]

-- | muda a estadogloss 
mudaDeEstado :: EstadoGloss -> EstadoGloss
mudaDeEstado e = mudaEstadoGloss mapa (imagemBIFundo e) (botIsActive e)
                where mapa = if length listaMapas > (mapaInt e +1) then mapaInt e +1 else 0

-- | Estado Inicial
mudaEstadoGloss :: Int -> (Picture,Picture) -> Maybe Int -> EstadoGloss
mudaEstadoGloss n (bI,f) botInfo = EstadoGloss estadoInicial estadoInicial n (bI,f) (mkMapa estadoInicial bI f) InGame botInfo [] 0
                        where estadoInicial = encontraIndiceLista n listaMapas     
